pragma solidity ^0.4.4;

contract customer {

  struct person {
    uint bankaccountNumber;
    uint amount;
    address merchant;
  }

  mapping (address => person) customer;

  function saveCustomerTransaction(address _person, address _merchant, uint _amount, uint bankacntID) {
    person memory user;
    user.bankaccountNumber = bankacntID;
    user.amount = _amount;
    user.merchant = _merchant;

    customer[_person] = user;
  }

  function getCutsomerTransaction(address _customer) constant returns (uint, uint, address) {
    person memory getCustomer;
    getCustomer = customer[_customer];
    return (getCustomer.bankaccountNumber, getCustomer.amount, getCustomer.merchant);
  }
}
